import { Component, OnInit } from '@angular/core';
import { ResourceService } from '../services/resource.service';
import { Resource } from '../models/resource.model';

@Component({
  selector: 'app-resource',
  templateUrl: './resource.component.html',
  styleUrls: ['./resource.component.css']
})
export class ResourceComponent implements OnInit {
  resources: Resource[] = [];

  constructor(private resourceService: ResourceService) {}

  ngOnInit(): void {
    this.fetchResources();
  }

  fetchResources(): void {
    this.resourceService.getResources().subscribe((data) => {
      this.resources = data;
    });
  }
}
<div class="resources-container">
  <h2>Disaster Resources</h2>

  <div *ngFor="let res of resources" class="resource-card">
    <h3>{{ res.name }} <small>({{ res.type }})</small></h3>

    <p><strong>Disaster ID:</strong> {{ res.disaster_id }}</p>

    <p *ngIf="res.location_name">
      <strong>Location:</strong> {{ res.location_name }}
    </p>

    <p><strong>Geo Data:</strong> {{ res.location }}</p>

    <small class="timestamp">Created: {{ res.created_at | date: 'medium' }}</small>
  </div>
</div>
